import React from 'react'

class Counter extends React.Component{
    state = {
        counterValue: 0
    }
    onIncrement = () => {
        this.setState({counterValue: this.state.counterValue + 1})
    }

    onDecrement = () => {
        this.setState({counterValue: this.state.counterValue - 1})
    }

    render() {
        return (
            <div>
                <h1>Counter Component</h1>
                <h3>Counter Value: {this.state.counterValue}</h3>
                <div style={{margin: 40, flexDirection: 'row'}}>
                    <button style={{width: 90, height: 60, borderRadius: 10, padding:10}} onClick={this.onIncrement}>Increment</button>
                    <button style={{width: 90, height: 60, borderRadius: 10, padding:10}} onClick={this.onDecrement}>Decrement</button>
                </div>
            </div>
        )
    }
}

export default Counter
